// Step 1: Extract input data
const input = $input.first().json;
console.log('=== Webhook Input ===');
console.log('Keys:', Object.keys(input));

let clientName, fileData;

if (input.body) {
  clientName = input.body.clientName;
  fileData = input.body.fileData;
} else {
  clientName = input.clientName;
  fileData = input.fileData;
}

console.log('Client Name:', clientName);
console.log('File Data length:', fileData ? fileData.length : 0);

if (!clientName || !fileData) {
  throw new Error(`Missing data: clientName=${!!clientName}, fileData=${!!fileData}`);
}

return [{
  json: {
    clientName: clientName,
    fileData: fileData
  }
}];

// Step 2: Convert base64 to binary
const data = $input.first().json;
const clientName = data.clientName;
const base64Data = data.fileData;

console.log('=== Converting to Binary ===');
console.log('Base64 length:', base64Data.length);

if (!/^[A-Za-z0-9+/=]+$/.test(base64Data)) {
  throw new Error('Invalid base64 data');
}

const binaryData = {
  data: base64Data,
  mimeType: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
  fileName: 'uploaded.xlsx'
};

console.log('Binary created successfully');

return [{
  json: {
    clientName: clientName
  },
  binary: {
    file: binaryData
  }
}];

// Step 4: Parse requirements
const items = $input.all();
const clientName = $('Create Binary').first().json.clientName;
const binaryData = $('Create Binary').first().binary;

console.log('=== Processing Requirements ===');
console.log('Client:', clientName);
console.log('Rows received:', items.length);

const requirements = [];
const categoryGroups = {};

items.forEach((item, index) => {
  const reqId = item.json['Requirement ID'];
  const category = item.json['Category'];
  const businessReq = item.json['Business Requirement'];
  const notes = item.json['Additional Notes'];

  if (!category && !businessReq) return;

  const requirement = {
    id: reqId || `REQ-${index + 1}`,
    category: category || 'General',
    requirement: businessReq || '',
    notes: notes || ''
  };

  requirements.push(requirement);

  const cat = requirement.category;
  if (!categoryGroups[cat]) {
    categoryGroups[cat] = [];
  }
  categoryGroups[cat].push(requirement);
});

let formattedRequirements = '# BUSINESS REQUIREMENTS DOCUMENT\n\n';
formattedRequirements += `**Client:** ${clientName}\n`;
formattedRequirements += `**Total Requirements:** ${requirements.length}\n\n`;
formattedRequirements += '## REQUIREMENTS BY CATEGORY\n\n';

Object.keys(categoryGroups).forEach(category => {
  const reqs = categoryGroups[category];
  formattedRequirements += `### ${category} (${reqs.length} requirements)\n\n`;

  reqs.forEach(req => {
    if (req.id) formattedRequirements += `**[${req.id}]**\n`;
    if (req.requirement) formattedRequirements += `- **Requirement:** ${req.requirement}\n`;
    if (req.notes) formattedRequirements += `- **Notes:** ${req.notes}\n`;
    formattedRequirements += '\n';
  });
});

console.log('Requirements parsed:', requirements.length);

return [{
  json: {
    clientName: clientName,
    totalRequirements: requirements.length,
    categories: Object.keys(categoryGroups),
    requirements: requirements,
    formattedRequirements: formattedRequirements
  },
  binary: binaryData
}];

const parseNode = $('Parse Requirements').first();
if (!parseNode?.json) return { error: 'No requirements data available' };
return parseNode.json.formattedRequirements;

const items = $input.all();
const clientName = $('Parse Requirements').first().json.clientName;
const binaryData = $('Parse Requirements').first().binary;

console.log('=== Parsing SDR Output ===');

let rawOutput = '';
if (items[0]?.json?.output) {
  rawOutput = items[0].json.output;
} else if (items[0]?.json?.text) {
  rawOutput = items[0].json.text;
} else {
  rawOutput = JSON.stringify(items[0].json);
}

console.log('Raw output length:', rawOutput.length);

let sdrData;
try {
  const jsonMatch = rawOutput.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    sdrData = JSON.parse(jsonMatch[0]);
  } else {
    sdrData = JSON.parse(rawOutput);
  }
} catch (error) {
  console.error('Failed to parse SDR output:', error);
  throw new Error(`Failed to parse SDR output. Raw: ${rawOutput.substring(0, 500)}`);
}

if (!sdrData.evars || !sdrData.props || !sdrData.events) {
  throw new Error('SDR output missing required fields');
}

console.log('Parsed SDR:');
console.log('- eVars:', sdrData.evars.length);
console.log('- Props:', sdrData.props.length);
console.log('- Events:', sdrData.events.length);

return [{
  json: {
    clientName: clientName,
    sdrData: sdrData,
    stats: {
      evars: sdrData.evars.length,
      props: sdrData.props.length,
      events: sdrData.events.length
    }
  },
  binary: binaryData
}];

const ExcelJS = require('exceljs');
const sdrOutput = $input.first().json;
const clientName = sdrOutput.clientName;
const sdrData = sdrOutput.sdrData;

const binaryData = $input.first().binary;
const binaryKey = Object.keys(binaryData)[0];
const fileData = binaryData[binaryKey];

console.log('=== Writing SDR to Excel ===');
console.log('Binary key:', binaryKey);
console.log('Client:', clientName);

const workbook = new ExcelJS.Workbook();
await workbook.xlsx.load(Buffer.from(fileData.data, 'base64'));

const eVarsSheet = workbook.getWorksheet('eVars');
const propsSheet = workbook.getWorksheet('Props');
const eventsSheet = workbook.getWorksheet('Events');

if (!eVarsSheet || !propsSheet || !eventsSheet) {
  throw new Error('Required sheets not found in Excel file');
}

console.log('Writing data to sheets...');

// Write eVars starting from row 7
let evarRow = 7;
sdrData.evars.forEach(evar => {
  const row = eVarsSheet.getRow(evarRow);
  row.getCell(2).value = evar['Requirement ID'] || '';
  row.getCell(3).value = evar['Analytics Variable'] || '';
  row.getCell(4).value = evar['Business Name'] || '';
  row.getCell(5).value = evar['Business Description'] || '';
  row.getCell(6).value = evar['Expected Values'] || '';
  row.getCell(7).value = evar['Implementation Trigger'] || '';
  row.getCell(8).value = evar['Example Value'] || '';
  row.getCell(9).value = evar['Additional Notes'] || '';
  row.commit();
  evarRow++;
});

console.log('eVars written:', sdrData.evars.length);

// Write Props starting from row 7
let propRow = 7;
sdrData.props.forEach(prop => {
  const row = propsSheet.getRow(propRow);
  row.getCell(2).value = prop['Requirement ID'] || '';
  row.getCell(3).value = prop['Analytics Variable'] || '';
  row.getCell(4).value = prop['Business Name'] || '';
  row.getCell(5).value = prop['Business Description'] || '';
  row.getCell(6).value = prop['Expected Values'] || '';
  row.getCell(7).value = prop['Implementation Trigger'] || '';
  row.getCell(8).value = prop['Example Value'] || '';
  row.getCell(9).value = prop['Additional Notes'] || '';
  row.commit();
  propRow++;
});

console.log('Props written:', sdrData.props.length);

// Write Events starting from row 7
let eventRow = 7;
sdrData.events.forEach(event => {
  const row = eventsSheet.getRow(eventRow);
  row.getCell(2).value = event['Requirement ID'] || '';
  row.getCell(3).value = event['Analytics Variable'] || '';
  row.getCell(4).value = event['Business Name'] || '';
  row.getCell(5).value = event['Business Description'] || '';
  row.getCell(6).value = event['Expected Values'] || '';
  row.getCell(7).value = event['Implementation Trigger'] || '';
  row.getCell(8).value = event['Example Value'] || '';
  row.getCell(9).value = event['Additional Notes'] || '';
  row.commit();
  eventRow++;
});

console.log('Events written:', sdrData.events.length);

const buffer = await workbook.xlsx.writeBuffer();
const base64Data = buffer.toString('base64');

const timestamp = new Date().toISOString().split('T')[0];
const fileName = `SDR_${clientName.replace(/[^a-zA-Z0-9]/g, '_')}_${timestamp}.xlsx`;

console.log('Excel file generated:', fileName);

return [{
  json: {
    success: true,
    message: 'SDR generation complete',
    clientName: clientName,
    fileName: fileName,
    stats: sdrOutput.stats
  },
  binary: {
    data: {
      data: base64Data,
      mimeType: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
      fileName: fileName
    }
  }
}];
