// Agent #1Ïùò Ï∂úÎ†•ÏùÑ GSSHOP BRD Framework ÌòïÏãùÏúºÎ°ú Î≥ÄÌôò
const agentOutput = $input.first().json.output || $input.first().json.text || '';
const context = $('Format for AI Agent').first().json;

console.log('=== Parsing for GSSHOP BRD Format ===');
console.log('Output length:', agentOutput.length);

if (!agentOutput || agentOutput.length < 100) {
  throw new Error('No valid output from Agent #1');
}

// BRD Framework Íµ¨Ï°∞: 6Í∞ú Ïª¨Îüº
const rows = [];

// Row 1: Ìó§Îçî
rows.push({
  'Business Goal': 'Business Goal',
  'Strategy': 'Strategy',
  'Initiative': 'Initiative',
  'Tactics': 'Tactics',
  'KPI': 'KPI',
  'Business Capability': 'Business Capability'
});

// Markdown ÌååÏã±ÌïòÏó¨ Í≥ÑÏ∏µ Íµ¨Ï°∞ Ï∂îÏ∂ú
const lines = agentOutput.split('\n');

let currentGoal = '';
let currentStrategy = '';
let currentInitiative = '';
let currentTactic = '';
let currentKPI = '';
let currentCapability = '';

lines.forEach(line => {
  const trimmed = line.trim();

  if (!trimmed) return;

  // Level 1: Business Goal (# Î°ú ÏãúÏûë)
  if (trimmed.match(/^# Business Goal \d+:/)) {
    currentGoal = trimmed.replace(/^# Business Goal \d+:\s*/, '');
    currentStrategy = '';
    currentInitiative = '';
    currentTactic = '';
    currentKPI = '';
    currentCapability = '';
  }
  // Level 2: Strategy (## Î°ú ÏãúÏûë)
  else if (trimmed.match(/^## Strategy \d+\.\d+:/)) {
    currentStrategy = trimmed.replace(/^## Strategy \d+\.\d+:\s*/, '');
    currentInitiative = '';
    currentTactic = '';
    currentKPI = '';
    currentCapability = '';
  }
  // Level 3: Initiative (### Î°ú ÏãúÏûë)
  else if (trimmed.match(/^### Initiative \d+\.\d+\.\d+:/)) {
    currentInitiative = trimmed.replace(/^### Initiative \d+\.\d+\.\d+:\s*/, '');
    currentTactic = '';
    currentKPI = '';
    currentCapability = '';
  }
  // Level 4: Tactic (#### Î°ú ÏãúÏûë)
  else if (trimmed.match(/^#### Tactic \d+\.\d+\.\d+\.\d+:/)) {
    currentTactic = trimmed.replace(/^#### Tactic \d+\.\d+\.\d+\.\d+:\s*/, '');
    currentKPI = '';
    currentCapability = '';
  }
  // Level 5: KPI
  else if (trimmed.match(/^\*\*KPIs?:\*\*/)) {
    const kpiContent = trimmed.replace(/^\*\*KPIs?:\*\*\s*/, '');
    currentKPI = kpiContent;
  }
  // Level 6: Business Capability
  else if (trimmed.match(/^\*\*Business Capabilities?:\*\*/)) {
    const capContent = trimmed.replace(/^\*\*Business Capabilities?:\*\*\s*/, '');
    currentCapability = capContent;

    // ÏôÑÏ†ÑÌïú ÌñâÏù¥ ÎêòÎ©¥ Ï∂îÍ∞Ä
    if (currentGoal && currentStrategy && currentInitiative && currentTactic) {
      rows.push({
        'Business Goal': currentGoal,
        'Strategy': currentStrategy,
        'Initiative': currentInitiative,
        'Tactics': currentTactic,
        'KPI': currentKPI,
        'Business Capability': currentCapability
      });
    }
  }
  // Bullet pointsÎ•º KPIÎÇò CapabilityÏóê Ï∂îÍ∞Ä
  else if (trimmed.startsWith('-') || trimmed.startsWith('*')) {
    const content = trimmed.replace(/^[-*]\s*/, '');

    // ÎßàÏßÄÎßâÏúºÎ°ú ÏÑ§Ï†ïÎêú Í≤ÉÏù¥ Î¨¥ÏóáÏù∏ÏßÄ ÌôïÏù∏
    if (currentCapability) {
      currentCapability += ' | ' + content;
    } else if (currentKPI) {
      currentKPI += ' | ' + content;
    }
  }
});

console.log('Total BRD rows parsed:', rows.length);

// Google Sheets Î©îÌÉÄÎç∞Ïù¥ÌÑ∞
const output = {
  spreadsheetName: `GSSHOP BRD - ${context.clientName} - ${new Date().toISOString().split('T')[0]}`,
  sheetName: 'GSSHOP BRD',
  rows: rows,
  metadata: {
    client: context.clientName,
    totalGoals: rows.filter(r => r['Business Goal'] && r['Business Goal'] !== 'Business Goal').length,
    generatedAt: new Date().toISOString()
  }
};

console.log('‚úÖ Parsed for GSSHOP BRD Format');
console.log('Total Goals:', output.metadata.totalGoals);

return [{ json: output }];

