// Parse SDR JSON output and prepare for Node.js service
const rawOutput = $input.first().json.output || $input.first().json.text;

let sdrData;
try {
  const jsonMatch = rawOutput.match(/\{[\s\S]*\}/);
  sdrData = jsonMatch ? JSON.parse(jsonMatch[0]) : JSON.parse(rawOutput);
} catch (error) {
  throw new Error('Failed to parse SDR output');
}

if (!sdrData.evars || !sdrData.props || !sdrData.events) {
  throw new Error('SDR missing required fields');
}

// Get original file data
const originalBinary = $('Create Binary').first().binary.file;
const clientName = $('Parse Requirements').first().json.clientName;

return [{
  json: {
    success: true,
    clientName: clientName,
    originalFileBase64: originalBinary.data,
    sdr: sdrData,
    stats: {
      evars: sdrData.evars.length,
      props: sdrData.props.length,
      events: sdrData.events.length
    }
  }
}];
